(function(){var Builder,Events,gridTdUpdated,placeholderTextForResource,resourceNames,updateGridTd,updatePlacehodlerText;Builder=ORBEON.Builder;Events=ORBEON.xforms.Events;resourceNames=["label","hint","text"];gridTdUpdated=[];placeholderTextForResource={};updateGridTd=function(gridTd){gridTd=$(gridTd);return _.each(resourceNames,function(lhha){var elementInDiv;elementInDiv=gridTd.find(".xforms-"+lhha);if(elementInDiv.is(".xforms-output")){elementInDiv=elementInDiv.children(".xforms-output-output");}return elementInDiv.attr("placeholder",placeholderTextForResource[lhha]);});};updatePlacehodlerText=function(){var foundDifference,isInDoc;foundDifference=false;_.each(resourceNames,function(lhha){var newText;newText=$(".fb-message-enter-"+lhha).children().text();if(newText!==placeholderTextForResource[lhha]){placeholderTextForResource[lhha]=newText;return foundDifference=true;}});if(foundDifference){isInDoc=function(e){return $.contains(document.body,e);};gridTdUpdated=_.filter(gridTdUpdated,isInDoc);return _.each(gridTdUpdated,updateGridTd);}};$(function(){updatePlacehodlerText();Events.ajaxResponseProcessedEvent.subscribe(updatePlacehodlerText);return Builder.mouseEntersGridTdEvent.subscribe(function(_arg){var gridTd;gridTd=_arg.gridTd;if(!_.contains(gridTdUpdated,gridTd)){gridTdUpdated.push(gridTd);return updateGridTd(gridTd);}});});}).call(this);